import React from 'react'

export const AboutDescription = () => {
    const calculateYears = (date) => {
        const today = new Date();
        const monthDifference = today.getMonth() - date.getMonth();
        let dateDifference = today.getFullYear() - date.getFullYear();

        //Checks if birthday has occurred this year or not.
        if (monthDifference < 0 || (monthDifference === 0 && today.getDate() < date.getDate())) dateDifference--;

        return dateDifference;
    };

    return (
        <p className="About-Description">I am a {calculateYears(new Date(2004, 7, 16))}-year-old with {calculateYears(new Date(2023, 0, 17))} year(s) of experience. My interest in coding began in my second semester of Pellissippi when I took the CISP 1010 course as I was unsure of what to major in. As the semester went by, I fell in love with coding and decided to become a Computer Science major. While my background is lacking compared to others, I strive to improve myself constantly and broaden my skills. I am intrigued by artificial intelligence and machine learning, so I plan to minor in artificial intelligence along with my CS major. I hope to gain practical experience and mentorship via internships and be able to help others utilizing this knowledge by becoming a TA.</p>
    );
};
