import React from 'react';

export const AboutDescription = () => {
    const calculateAge = () => {
        const birthday = new Date(2005, 7, 16); //August 16, 2004.
        const today = new Date();
        const monthDifference = today.getMonth() - birthday.getMonth();
        let age = today.getFullYear() - birthday.getFullYear();

        //Checks if birthday has occurred this year or not.
        if (monthDifference < 0 || (monthDifference === 0 && today.getDate() < birthday.getDate())) age--;

        return age;
    };

    return (
        <p className="About-Description">I am a {calculateAge()}-year-old</p>
    );
};
