import React, { useState, useEffect, useRef } from "react"
import "../App.css"

export const NavigationBar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [activeIndex, setActiveIndex] = useState(null);
  const homeRef = useRef(null);
  const aboutRef = useRef(null);
  const projectsRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY >= 40);
    };
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const scrollToSection = (ref) => {
    if (ref && ref.current) {
      window.scrollTo({
        top: ref.current.offsetTop,
        behavior: "smooth"
      });
    }
  };

  const handleNavigationClick = (index) => {
    setActiveIndex(index);
    switch(index) {
      case 0:
        scrollToSection(homeRef);
        break;
      case 1:
        scrollToSection(aboutRef);
        break;
      case 2:
        scrollToSection(projectsRef);
        break;
      default:
        break;
    }
  };

  const navigatorItems = [
    { name: "Home" },
    { name: "About" },
    { name: "Projects" },
  ];

  const navLinksClass = isScrolled ? "nav-links-filled" : "nav-links"; //Checks whether the navigationBar scrolled to the section.
  const navSliderClass = activeIndex !== null ? `nav-slider ${navigatorItems[activeIndex].name}` : "nav-slider"; //If a navigationItem has been clicked from the navigationBar, set the class of the clicked navigationItem.

  return (
    <div className="nav">
      <div className={navLinksClass}>
        {navigatorItems.map((item, index) => (
          <span key={index} onClick={() => handleNavigationClick(index)} className={`nav-links-item ${activeIndex === index ? "active" : ""}`}>
            {item.name}
          </span>
        ))}
        <div className={navSliderClass} />
      </div>
    </div>
  );
}

export default NavigationBar;
