export const AboutDescription = () => {
    calculateAge = () => {
        let today = new Date();
        let age = today.getFullYear() - birthday.getFullYear();
        let monthDifference = now.getMonth() - birthday.getMonth();

        //Checks to see if birthday has occured this year or not.
        if(monthDifference < 0 || (monthDifference === 0 && now.getDate() < birthday.getDate())) age--;

        return age;
    }



    return (
        <p className="About-Description">I am a {age}-year-old</p>
    )
}
